function initSearch(){const e=document.querySelector("#search-form"),n=document.querySelector("#search-input");if(!e||!n)return;const t=Core.debounce(function(e){e.length>2&&performSearch(e)},300);n.addEventListener("input",function(){t(this.value)}),e.addEventListener("submit",function(e){e.preventDefault();const t=n.value.trim();t&&performSearch(t)})}function performSearch(e){console.log("Searching for:",e)}document.addEventListener("DOMContentLoaded",function(){initSearch()});